var CrispyPredictsEngine = Class.create();
CrispyPredictsEngine.prototype = {
	initialize: function() {
	},

	processData : function(transcriptSubstr) {
		var request = new sn_ws.RESTMessageV2();
		request.setEndpoint('https://hackathon130.service-now.com/api/now/agent_intelligence/solution/ml_x_snc_global_global_meeting_mentions/prediction?top_n=3&short_description=' + transcriptSubstr + '&description=' + transcriptSubstr +'');
		request.setHttpMethod('GET');

		//Eg. UserName="admin", Password="admin" for this code sample.
		var user = 'steele';
		var password = 'LarraWins';

		request.setBasicAuth(user,password);
		request.setRequestHeader("Accept","application/json");

		var response = request.execute();
		return response.getBody();

	},

	type: 'CrispyPredictsEngine'
};
