sys_ui_action.script:
var newInteraction = current.insert();

var notify = new sn_notify.NotifyScoped();
var conference = notify.conferenceCall();
var participants = current.participants.split(',');

	var confUtils = new global.NotifyConferenceUtils();
	var data = confUtils.getConferenceInputDataTemplate();
	data.table = current.getTableName();
	data.sysId = newInteraction.toString();
	data.addToWorkNotes = true;
	data.confId = conference.sys_id;
	data.message = current.short_description;
data.isNewConference = true;
data.serviceProvider = "Zoom";
var x;
var itemObj = {};
for(x=0; x <participants.length ; x++){
	gs.addInfoMessage(GlideStringUtil.isEligibleSysID(participants[x]))
	if(GlideStringUtil.isEligibleSysID(participants[x])){
		itemObj.id = participants[x];
		data.items.push(itemObj);
	}else if(participants[x].indexOf("@") != -1){
		itemObj.email = participants[x];
		data.items.push(itemObj);
	}
}
	gs.addInfoMessage(JSON.stringify(data))
	//var result = confUtils.doConferenceAction('start', data);
var zoomCon = new sn_notify_zoom.NotifyJSZoomConferenceSNCAPI();
var result = zoomCon.startConferenceFromData(data);
gs.addInfoMessage(JSON.stringify(result))
